// Function to extract messages and feedback status, then save to CSV file
function exportMessagesToCSV(data) {
    // Initialize CSV content with headers
    let csvContent = "Id,Timestamp,Type,Message,Feedback\n";

    data.forEach(session => {
        session.messages.forEach(message => {
            // Add message content and feedback status to CSV content
            csvContent += `${session.sessionId},${new Date(message.time).toLocaleDateString()},${message.role == 'bot' ? 'Answer' : 'Question' },"${message.content}",${message.feedback ? message.feedback : 'n/a'}\n`;
        })
    });

    // Write CSV content to file
    fs.writeFileSync("messages-dev.csv", csvContent, 'utf8');
    console.log("CSV file 'messages.csv' has been created successfully.");
}

// Call the function to export data
exportMessagesToCSV(data);